dataset.sql:
( with temp as( SELECT avg( ABS(TIMESTAMPDIFF(DAY , ist.yi_shang_xian , ist.chu_li_zhong) ) ) current_month, DATE_FORMAT(ri.created_on, '%Y-%m') months FROM issues_status_trigger ist left join redmine_issues ri on ist.issues_id = ri.id WHERE ri.project_id =( select id from redmine_projects rp WHERE rp.identifier = :teamName) AND ri.tracker_id =( SELECT id from redmine_trackers WHERE name = '需求' ) and ri.status_id =( select id from issue_statuses is2 WHERE is2.name = '已上线') GROUP by months order by months asc
) SELECT *, if(ROUND(IFNULL(( t.current_month-t.before_month )/ t.before_month, 0),2)<0, concat(ROUND(IFNULL(( t.current_month-t.before_month )/ t.before_month, 0),2),"(下降)"), ROUND(IFNULL(( t.current_month-t.before_month )/ t.before_month, 0),2) ) xiangBi, "需求交付周期(天)" as 'name' from ( select temp.months as months , round(temp.current_month,2) current_month, round(lag(temp.current_month,1) over( order by temp.months)) before_month
from temp )t ) union all
( with temp as( SELECT DATE_FORMAT(cm.time_stamp , '%Y-%m') months, avg(cm.val)/ 100 current_month FROM ci_metric cm left join ci_pipeline_history cph on cm.pipeline_id = cph.id WHERE cph.project_id in( SELECT id from ci_project cp WHERE cp.team_id =( SELECT id from ci_team ct WHERE ct.name = :teamName) ) AND cm.type = 'new_unit_test_coverage_rate' AND cph.target_branch = 'master' GROUP by months order by months asc ) SELECT *, if(ROUND(IFNULL(( t.current_month-t.before_month )/ t.before_month, 0),2)<0, concat(ROUND(IFNULL(( t.current_month-t.before_month )/ t.before_month, 0),2),"(下降)"), ROUND(IFNULL(( t.current_month-t.before_month )/ t.before_month, 0),2) ) xiangBi, "新增单元测试覆盖率" as 'name' from ( select temp.months, round(temp.current_month,2) current_month, round(lag(temp.current_month,1) over( order by temp.months)) before_month from temp )t
)
union all
( with temp as( with t as( select z1.project_name , z1.profile_name , FROM_UNIXTIME(z1.red_start/1000) as red_start, FROM_UNIXTIME(z1.red_end/1000) as red_end, (z1.red_end-z1.red_start)/(60 * 1000) as spend_time from ( select project_name, target_branch, profile_name, min(red_time) as red_start, repair_time as red_end from ( SELECT t1.project_name, t1.target_branch, t1.profile_name, t1.end_time_millis AS red_time, MIN(h2.end_time_millis) AS repair_time FROM ( SELECT h1.end_time_millis, h1.project_name, h1.target_branch, h1.profile_id, h1.status, h1.source_branch , p1.name AS profile_name FROM ci_pipeline_history h1, ci_pipeline_profile p1 WHERE p1.id = h1.profile_id and p1.team_name = :teamName AND h1.status = 5 ) t1, ci_pipeline_history h2, ci_project p2, ci_team t2 WHERE h2.project_name = t1.project_name AND (h2.target_branch = t1.target_branch or h2.source_branch= t1.source_branch ) AND h2.profile_id = t1.profile_id and h2.project_id=p2.id and p2.team_id = t2.id and t2.name=:teamName AND h2.status != 5 AND h2.end_time_millis>t1.end_time_millis GROUP BY t1.end_time_millis, t1.project_name, t1.target_branch, t1.profile_name order by red_time desc ) z group by project_name, target_branch, profile_name, red_end ) z1
)
SELECT DATE_FORMAT(t.red_start,'%Y-%m') months, avg(t.spend_time) current_month from t group by months order by months asc )
SELECT *, if(ROUND(IFNULL(( t.current_month-t.before_month )/ t.before_month, 0),2)<0, concat(ROUND(IFNULL(( t.current_month-t.before_month )/ t.before_month, 0),2),"(下降)"), ROUND(IFNULL(( t.current_month-t.before_month )/ t.before_month, 0),2) ) xiangBi, "红灯修复时长(分钟)" as 'name' from ( select temp.months, round(temp.current_month,2) current_month, round(lag(temp.current_month,1) over( order by temp.months)) before_month -- ( temp.current_month-lag(temp.current_month,12) over( order by temp.months) )/lag(temp.current_month,12) over( order by temp.months) xiangBi from temp )t
)
union all
( with temp as( SELECT sum( mr) current_month, DATE_FORMAT(t.time,'%Y-%m') months from ( SELECT sum(cus.mergedmrs) as 'mr', cus.time as 'time' from ( select STR_TO_DATE(cus.`day`,'%Y-%m-%d') as "time", cus.* FROM ci_user_statistic cus, ci_project p, ci_team t where cus.project_name =p.name and p.team_id =t.id and t.name=:teamName ) cus where name_space = :teamName and user_name !='robot' group by cus.day order by cus.day
)t GROUP by months
)
SELECT *, if(ROUND(IFNULL(( t.current_month-t.before_month )/ t.before_month, 0),2)<0, concat(ROUND(IFNULL(( t.current_month-t.before_month )/ t.before_month, 0),2),"(下降)"), ROUND(IFNULL(( t.current_month-t.before_month )/ t.before_month, 0),2) ) xiangBi, "集成频率(次)" as 'name' from ( select temp.months, round(temp.current_month,2) current_month, round(lag(temp.current_month,1) over( order by temp.months)) before_month -- ( temp.current_month-lag(temp.current_month,12) over( order by temp.months) )/lag(temp.current_month,12) over( order by temp.months) xiangBi from temp )t )
union all
( with temp as( select max(acr.coverage_rate) current_month, DATE_FORMAT(acr.create_time,'%Y-%m') months from grafana.api_coverage_rate acr
where acr.team_id=(SELECT group_id from ci_team ct WHERE ct.name=:teamName) GROUP by months order by months asc )
SELECT *, if(ROUND(IFNULL(( t.current_month-t.before_month )/ t.before_month, 0),2)<0, concat(ROUND(IFNULL(( t.current_month-t.before_month )/ t.before_month, 0),2),"(下降)"), ROUND(IFNULL(( t.current_month-t.before_month )/ t.before_month, 0),2) ) xiangBi, '接口自动化测试覆盖率(%)' as 'name' from ( select temp.months, round(temp.current_month,2) current_month, round(lag(temp.current_month,1) over( order by temp.months)) before_month from temp )t )

( with temp as( SELECT avg( ABS(TIMESTAMPDIFF(DAY , ist.yi_shang_xian , ist.chu_li_zhong) ) ) current_month, DATE_FORMAT(ri.created_on, '%Y-%m') months FROM issues_status_trigger ist left join redmine_issues ri on ist.issues_id = ri.id WHERE ri.project_id =( select id from redmine_projects rp WHERE rp.identifier = :teamName) AND ri.tracker_id =( SELECT id from redmine_trackers WHERE name = '需求' ) and ri.status_id =( select id from issue_statuses is2 WHERE is2.name = '已上线') GROUP by months order by months asc
) SELECT *, IFNULL(( t.current_month-t.before_month )/ t.before_month, 0) xiangBi, "需求交付周期" as 'name' from ( select temp.months, temp.current_month, lag(temp.current_month, 1) over( order by temp.months) before_month -- ( temp.current_month-lag(temp.current_month,12) over( order by temp.months) )/lag(temp.current_month,12) over( order by temp.months) xiangBi from temp )t ) union all
( with temp as( SELECT DATE_FORMAT(cm.time_stamp , '%Y-%m') months, avg(cm.val)/ 100 current_month FROM ci_metric cm left join ci_pipeline_history cph on cm.pipeline_id = cph.id WHERE cph.project_id in( SELECT id from ci_project cp WHERE cp.team_id =( SELECT id from ci_team ct WHERE ct.name = :teamName) ) AND cm.type = 'new_unit_test_coverage_rate' AND cph.target_branch = 'master' GROUP by months order by months asc ) SELECT *, IFNULL(( t.current_month-t.before_month )/ t.before_month, 0) xiangBi, "新增单元测试覆盖率" as 'name' from ( select temp.months, temp.current_month, lag(temp.current_month, 1) over( order by temp.months) before_month -- ( temp.current_month-lag(temp.current_month,12) over( order by temp.months) )/lag(temp.current_month,12) over( order by temp.months) xiangBi from temp )t
)
union all
( with temp as( with t as( select z1.project_name , z1.profile_name , FROM_UNIXTIME(z1.red_start/1000) as red_start, FROM_UNIXTIME(z1.red_end/1000) as red_end, (z1.red_end-z1.red_start)/(60 * 1000) as spend_time from ( select project_name, target_branch, profile_name, min(red_time) as red_start, repair_time as red_end from ( SELECT t1.project_name, t1.target_branch, t1.profile_name, t1.end_time_millis AS red_time, MIN(h2.end_time_millis) AS repair_time FROM ( SELECT h1.end_time_millis, h1.project_name, h1.target_branch, h1.profile_id, h1.status, h1.source_branch , p1.name AS profile_name FROM ci_pipeline_history h1, ci_pipeline_profile p1 WHERE p1.id = h1.profile_id and p1.team_name = :teamName AND h1.status = 5 ) t1, ci_pipeline_history h2, ci_project p2, ci_team t2 WHERE h2.project_name = t1.project_name AND (h2.target_branch = t1.target_branch or h2.source_branch= t1.source_branch ) AND h2.profile_id = t1.profile_id and h2.project_id=p2.id and p2.team_id = t2.id and t2.name=:teamName AND h2.status != 5 AND h2.end_time_millis>t1.end_time_millis GROUP BY t1.end_time_millis, t1.project_name, t1.target_branch, t1.profile_name order by red_time desc ) z group by project_name, target_branch, profile_name, red_end ) z1
)
SELECT DATE_FORMAT(t.red_start,'%Y-%m') months, avg(t.spend_time) current_month from t group by months order by months asc )
SELECT *, IFNULL(( t.current_month-t.before_month )/t.before_month,0) xiangBi, "红灯修复时长" as 'name' from ( select temp.months, temp.current_month, lag(temp.current_month,1) over( order by temp.months) before_month -- ( temp.current_month-lag(temp.current_month,12) over( order by temp.months) )/lag(temp.current_month,12) over( order by temp.months) xiangBi from temp )t
)
union all
( with temp as( SELECT sum( mr) current_month, DATE_FORMAT(t.time,'%Y-%m') months from ( SELECT sum(cus.mergedmrs) as 'mr', cus.time as 'time' from ( select STR_TO_DATE(cus.`day`,'%Y-%m-%d') as "time", cus.* FROM ci_user_statistic cus, ci_project p, ci_team t where cus.project_name =p.name and p.team_id =t.id and t.name=:teamName ) cus where name_space = :teamName and user_name !='robot' group by cus.day order by cus.day
)t GROUP by months
)
SELECT *, if (IFNULL(( t.current_month-t.before_month )/t.before_month,0)<0, ROUND(IFNULL(( t.current_month-t.before_month )/t.before_month,0),2), ROUND( IFNULL(( t.current_month-t.before_month )/t.before_month,0),2) ) xiangBi, "集成频率" as 'name' from ( select temp.months, temp.current_month, lag(temp.current_month,1) over( order by temp.months) before_month -- ( temp.current_month-lag(temp.current_month,12) over( order by temp.months) )/lag(temp.current_month,12) over( order by temp.months) xiangBi from temp )t )
union all
( with temp as( select max(acr.coverage_rate) current_month, DATE_FORMAT(acr.create_time,'%Y-%m') months from grafana.api_coverage_rate acr
where acr.team_id=(SELECT group_id from ci_team ct WHERE ct.name=:teamName) GROUP by months order by months asc )
SELECT *, if (IFNULL(( t.current_month-t.before_month )/t.before_month,0)<0, concat(ROUND(IFNULL(( t.current_month-t.before_month )/t.before_month,0),2) ,"(下降)"), ROUND( IFNULL(( t.current_month-t.before_month )/t.before_month,0),2) ) xiangBi, '接口自动化测试覆盖率' as 'name' from ( select temp.months, temp.current_month, lag(temp.current_month,1) over( order by temp.months) before_month from temp )t )
